mapper.select:
select id "id", code "code", pcode "pcode", name "name", icon "icon", url "url", levels "levels" from tfw_menu where id in
#{menuId}
and alias='no' order by levels,num asc

select code, param_values value
from cache_init_param
where code like #{code}

	select KPI_CODE,
           sum(coalesce(KPI_VALUE, 0)) dy, 
           sum(coalesce(M_LM_VALUE, 0)) sy,
           sum(coalesce(Y_TY_VALUE, 0)) bnlj, 
           sum(coalesce(Y_LY_VALUE, 0)) qnlj 
           from
           ${table}m


                   inner join
                  (select  ct.channel_type from (select channel_type,mapping_id from dmcode_base_channel_type where mark='C') ct
                   where 1=1 and ct.mapping_id in

                    #{channelId}

                   ) ch
                   on m.channel_type=ch.channel_type

            where 1 = 1


                    AND m.prov_id = #{prov}


                    AND m.area_no = #{city}



                    AND m.MONTH_ID = #{date}



                  AND m.IS_GROUP in

                   #{clientId}




                  AND m.ACTIVITY_TYPE in

                   #{contractId}


                 group by m.KPI_CODE

select
m.prov_id m.is_group m.activity_type id, sum(coalesce(m.KPI_VALUE, 0)) dy, sum(coalesce(m.M_LM_VALUE, 0)) sy, sum(coalesce(m.Y_TY_VALUE, 0)) bnlj, sum(coalesce(m.Y_LY_VALUE, 0)) qnlj from ${table}m
inner join (select ct.channel_type from (select channel_type,mapping_id from dmcode_base_channel_type where mark='C') ct where 1=1 and ct.mapping_id in
#{channelId}
) ch on m.channel_type=ch.channel_type
where 1 = 1
AND m.prov_id = #{prov}
AND m.area_no = #{city}
AND m.KPI_CODE = #{kid}
AND m.MONTH_ID = #{date}
AND m.IS_GROUP in
#{clientId}
AND m.ACTIVITY_TYPE in
#{contractId}
group by
m.prov_id m.is_group m.activity_type

select dm.title,dm.id,c.* from ( select t.channel_type_name title,t.dimension_code id,t.ord from dw_dimension_name t where 1=1 and t.dimension_type_code='2' and t.dimension_code!='-1'
AND t.dimension_code in
#{clientId}
)dm, ( select * from ( select l.parent_code pid, nvl2(k.uatio,k.uatio,1)uatio,k.format,k.unit, to_char(to_number(l.space)+2) space, decode(k.kpi_code,null,'',k.kpi_code) kpiCode from dw_kpi_level l left join dw_kpi k on l.level_code=k.level_code where l.type='1') code where 1 = 1
AND code.kpiCode = #{kid} )c order by to_number(ord)

select dm.title,dm.id,c.* from (select l.parent_code pid, nvl2(k.uatio,k.uatio,1)uatio,k.format,k.unit, to_char(to_number(l.space)+2) space, decode(k.kpi_code,null,'',k.kpi_code) kpiCode from dw_kpi_level l left join dw_kpi k on l.level_code=k.level_code where 1=1 and l.type='1'
AND k.kpi_code = #{kid} )c, (select dimension_code id,channel_type_name title,ord from dw_dimension_name where dimension_type_code='3' and dimension_code !='-1'
and dimension_code in
#{channelId}
)dm order by to_number(ord)

select data.id,sum(coalesce(data.dy,0))dy, sum(coalesce(data.sy,0))sy, sum(coalesce(data.bnlj,0))bnlj,sum(coalesce(data.qnlj,0))qnlj from (select * from (select m.CHANNEL_TYPE, sum(coalesce(m.KPI_VALUE, 0)) dy, sum(coalesce(m.M_LM_VALUE, 0)) sy, sum(coalesce(m.Y_TY_VALUE, 0)) bnlj, sum(coalesce(m.Y_LY_VALUE, 0)) qnlj from ${table} m
inner join (select ct.channel_type from (select channel_type,mapping_id from dmcode_base_channel_type where mark='C') ct where 1=1 and ct.mapping_id in
#{channelId}
) ch on m.channel_type=ch.channel_type
where 1 = 1
AND m.prov_id = #{prov}
AND m.area_no = #{city}
AND m.KPI_CODE = #{kid}
AND m.MONTH_ID = #{date}
AND m.IS_GROUP in
#{clientId}
AND m.ACTIVITY_TYPE in
#{contractId}
group by m.CHANNEL_TYPE)d left join (select mapping_id id,channel_type from dmcode_base_channel_type where mark='C'
and mapping_id in
#{channelId}
)cp on cp.channel_type=d.CHANNEL_TYPE)data group by data.id

select dm.title,dm.id,c.* from ( select t.channel_type_name title,t.dimension_code id,t.ord from dw_dimension_name t where t.dimension_type_code='4' and t.dimension_code!='-1'
and t.dimension_code in
#{contractId}
)dm, ( select * from ( select l.parent_code pid, nvl2(k.uatio,k.uatio,1)uatio,k.format,k.unit, to_char(to_number(l.space)+2)space, decode(k.kpi_code,null,'',k.kpi_code) kpiCode from dw_kpi_level l left join dw_kpi k on l.level_code=k.level_code where l.type='1' ) code where 1 = 1
AND code.kpiCode = #{kid} )c order by to_number(ord)

select * from (
select area_id id,area_desc title,prov_id pid from dmcode_pub.dmcode_area_139 where 1=1 and ord_id <=139 and prov_id=#{proid}
select area_id id,area_desc title,prov_id pid from dmcode_pub.dmcode_area where 1=1 and prov_id=#{proid}
), (select to_char(to_number(l.space)+4) space, nvl2(k.uatio,k.uatio,1)uatio,k.format, k.unit, decode(k.kpi_code,null,'',k.kpi_code) kpiCode from dw_kpi_level l left join dw_kpi k on l.level_code=k.level_code where l.type='1'
AND k.kpi_code = #{kid} ) order by id asc

select m.AREA_NO id, sum(coalesce(m.KPI_VALUE, 0)) dy, sum(coalesce(m.M_LM_VALUE, 0)) sy, sum(coalesce(m.Y_TY_VALUE, 0)) bnlj, sum(coalesce(m.Y_LY_VALUE, 0)) qnlj from ${table} m
inner join (select ct.channel_type from (select channel_type,mapping_id from dmcode_base_channel_type where mark='C') ct where 1=1 and ct.mapping_id in
#{channelId}
) ch on m.channel_type=ch.channel_type
where 1 = 1
and m.prov_id = #{proid} and m.area_no!='-1'
AND m.MONTH_ID = #{date}
AND m.KPI_CODE = #{kid}
AND m.IS_GROUP in
#{clientId}
AND m.ACTIVITY_TYPE in
#{contractId}
group by m.AREA_NO

	 select *
  	from (select uatio,format from dw_kpi where type='1'

         and kpi_code= #{kid}
     )k,
     (select month_id id from dw_acct_month where 1 = 1
     AND MONTH_ID between
     #{eleDate}
     and #{date})d
     order by d.id

select sum(coalesce(m.KPI_VALUE,0)) dy,m.MONTH_ID id
    from ${table}m


               inner join
              (select  ct.channel_type from (select channel_type,mapping_id from dmcode_base_channel_type where mark='C') ct
               where 1=1 and ct.mapping_id in

                #{channelId}

               ) ch
               on m.channel_type=ch.channel_type

    where 1 = 1

        AND m.prov_id = #{prov}


         AND m.area_no = #{city}


    AND m.MONTH_ID between
    ${eleDate}
    and ${date}


        AND m.KPI_CODE = #{kid}



       AND m.IS_GROUP in

         #{clientId}




        AND m.ACTIVITY_TYPE in

            #{contractId}


    group by m.MONTH_ID

  	select * from
			(select format,uatio from dw_kpi where type='1'

          	AND KPI_CODE = #{kid}
      	),
           (select dimension_code id, channel_type_name title from dw_dimension_name
                  where dimension_type_code = '4' and dimension_code != '-1'

          		and  dimension_code in

                #{contractId}

          )

select sum(coalesce(m.KPI_VALUE,0)) dy,m.ACTIVITY_TYPE id
    from ${table}m


               inner join
              (select  ct.channel_type from (select channel_type,mapping_id from dmcode_base_channel_type where mark='C') ct
               where 1=1 and ct.mapping_id in

                #{channelId}

               ) ch
               on m.channel_type=ch.channel_type

    where 1 = 1

        AND m.prov_id = #{prov}


         AND m.area_no = #{city}



      AND m.MONTH_ID =#{date}



        AND m.KPI_CODE = #{kid}



       AND m.IS_GROUP in

           #{clientId}




        AND m.ACTIVITY_TYPE in

            #{contractId}


    group by m.ACTIVITY_TYPE

    	select * from
  			(select format,uatio from dw_kpi where type='1'

            	AND KPI_CODE = #{kid}
        	),
          (select dimension_code id, channel_type_name title from dw_dimension_name
               where dimension_type_code = '2' and dimension_code != '-1'

		           AND dimension_code in

		               #{clientId}

		      )

select sum(coalesce(m.KPI_VALUE,0)) dy,m.IS_GROUP id
    from ${table}m


               inner join
              (select  ct.channel_type from (select channel_type,mapping_id from dmcode_base_channel_type where mark='C') ct
               where 1=1 and ct.mapping_id in

                #{channelId}

               ) ch
               on m.channel_type=ch.channel_type

    where 1 = 1

        AND m.prov_id = #{prov}


         AND m.area_no = #{city}



     AND m.MONTH_ID = #{date}



        AND m.KPI_CODE = #{kid}



       AND m.IS_GROUP in

           #{clientId}




        AND m.ACTIVITY_TYPE in

            #{contractId}


    group by m.IS_GROUP

    	select sum(coalesce(r.dy, 0)) dy, r.id from
			(select * from
			 (select sum(coalesce(m.KPI_VALUE, 0)) dy, m.CHANNEL_TYPE
			  from ${table}m


                   inner join
                  (select  ct.channel_type from (select channel_type,mapping_id from dmcode_base_channel_type where mark='C') ct
                   where 1=1 and ct.mapping_id in

                    #{channelId}

                   ) ch
                   on m.channel_type=ch.channel_type

        where 1 = 1

            AND m.prov_id = #{prov}


             AND m.area_no = #{city}



         AND m.MONTH_ID = #{date}



            AND m.KPI_CODE = #{kid}



           AND m.IS_GROUP in

               #{clientId}




            AND m.ACTIVITY_TYPE in

                #{contractId}


        group by m.channel_type)data
        left join
        (select channel_type,mapping_id id from dmcode_base_channel_type where mark = 'C')c
		on c.CHANNEL_TYPE=data.CHANNEL_TYPE)r
		group by r.id

    	select * from
    	(select dimension_code id,channel_type_name title from dw_dimension_name
           where dimension_type_code = '3' and dimension_code != '-1'


               and dimension_code in

					#{channelId}

          ),
        (select format,uatio from dw_kpi where type='1'


	            AND KPI_CODE = #{kid}

        )

   	select unit from dw_kpi
   	where type = '1'


	            AND KPI_CODE = #{kid}
